package main 

import "fmt"
func merge(l[]int, r[]int) []int {
	var arr []int
	for len(l) > 0 && len(r) > 0 {
		if l[0] > r[0] {
			arr = append(arr, r[0])
			r = r[1:]
		} else {
			arr = append(arr, l[0])
			l = l[1:]
		}
	}
	arr = append(arr, r...)
	arr = append(arr, l...)
	return arr
}

func merge_sort(arr[]int) []int{
	if len(arr) == 1 { return arr }
	arr1, arr2 := arr[:int(len(arr)/2)], arr[int(len(arr)/2):]

	l, r := merge_sort(arr1), merge_sort(arr2)

	return merge(l, r)
}

